SELECT * FROM customer c WHERE c.cu_name IN (SELECT u.us_name FROM users u WHERE u.us_class = 'administrator') AND c.cu_fax = (SELECT s.su_fax FROM supplier s WHERE s.su_phone = '123456') AND c.cu_email IN (SELECT s1.su_email FROM supplier s1 WHERE s1.su_email LIKE '%H%');
