SELECT su_id , su_name FROM supplier s JOIN purchaseorder po ON s.su_id = po.po_su_id JOIN users u ON po.po_us_id = u.us_id JOIN movement m ON u.us_id = m.mo_us_id JOIN movement_details md ON m.mo_id = md.mod_mo_id JOIN item i ON md.mod_it_id = i.it_id JOIN invoicedetail ivd ON i.it_id = ivd.ind_it_id JOIN invoice iv ON ivd.ind_in_id = iv.in_id JOIN customer c ON iv.in_cu_id = c.cu_id WHERE su_name = cu_name;
2
SELECT su_id , su_name FROM supplier s JOIN purchaseorder po ON s.su_id = po.po_su_id JOIN users u ON po.po_us_id = u.us_id JOIN movement m ON u.us_id = m.mo_us_id JOIN movement_details md ON m.mo_id = md.mod_mo_id JOIN item i ON md.mod_it_id = i.it_id JOIN invoicedetail ivd ON i.it_id = ivd.ind_it_id JOIN invoice iv ON ivd.ind_in_id = iv.in_id JOIN customer c ON iv.in_cu_id = c.cu_id WHERE su_name = cu_name ORDER BY su_name;
